import './App.css';
import React, { useState } from 'react';
import AnalogueClock from 'react-analogue-clock';
import { Container, Row, Col } from 'reactstrap';

function App() {
    const [view_tick, set_tick] = useState(0);
    const clockOptions = {
        baseColor: '#ffffff',
        borderColor: '#000000',
        borderWidth: 2,
        centerColor: '#000000',
        handColors: {
            hour: '#000000',
            minute: '#000000',
            second: '#000000',
        },
        notchColor: '#000000',
        numbersColor: '#000000',
        showNumbers: true,
        size: 200
    }
    return (
        <div className="App">
            <header id="header">
                <a href="#">Home</a>
                <a href="#">Policy</a>
                <a href="#">Content</a>
            </header>
            <div id="main">
                {/* <h1 id="title-epoch">Epoch & Unix Timestamp Conversion Tools</h1>
                <div id="data-stream">
                    <b>
                        {tick(set_tick)}
                        {view_tick}
                    </b>
                </div>
                <br/>
                <div id="analog-stream">
                    < AnalogueClock {...clockOptions} />
                </div> */}
                <Container style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
                    <Row>
                        <Col><p id="title-epoch">Epoch & Unix Timestamp Conversion Tools</p></Col>
                        <Col>
                            <center id="analog-clock">
                                < AnalogueClock {...clockOptions} />
                            </center>
                        </Col>
                        <Col><h3>The Current Epoch Unix Timestamp</h3></Col>
                        <Col style={{fontSize:"20px"}}>
                            {tick(set_tick)}
                            {view_tick}SECONDS SINCE JAN 01 1970. (UTC)
                        </Col>
                    </Row>
                </Container>
            </div>
            <footer id="footer">
                <div id="footer-id">
                    <br/>powered by <a href="#" id="footer-id-a">todolib.in</a><br/><br/>
                    <p>Â© 2021 Todo Lib's & Gti<br/><br/>
                    Contact Us Privacy Policy</p>
                </div>
            </footer>
        </div>
    );
}

function tick(set_tick) {
    setTimeout(() => {
        set_tick(Math.round( new Date().getTime() / 1000));
    }, 1000)
}

export default App;
