import './App.css';
import React, { useState } from 'react';
import AnalogueClock from 'react-analogue-clock';
import { Container, Row, Col } from 'reactstrap';

function App() {
    const [view_tick, set_tick] = useState(0);
    const clockOptions = {
        baseColor: '#ffffff',
        borderColor: '#000000',
        borderWidth: 4,
        centerColor: '#000000',
        handColors: {
            hour: '#000000',
            minute: '#000000',
            second: '#000000',
        },
        notchColor: '#000000',
        numbersColor: '#000000',
        showNumbers: true,
        size: 250
    }
    return (
        <div className="App">
            <header id="header">
                <a href="#">Home</a>
                <a href="#">Policy</a>
                <a href="#">Content</a>
            </header>
            <div id="main">
                <Container style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <Row>
                        <Col><p id="title-epoch">Epoch & Unix Timestamp Conversion Tools</p></Col>
                        <Col>
                            <center id="analog-clock">
                                < AnalogueClock {...clockOptions} />
                            </center>
                        </Col>
                        <Col><h3>The Current Epoch Unix Timestamp</h3></Col>
                        <Col>
                            {tick(set_tick)}
                            <b style={{ fontSize: "40px" }}>{view_tick}</b><br/>SECONDS SINCE JAN 01 1970. (UTC)
                        </Col>
                        <Col>
                            <h3>Enter a Timestamp</h3>
                            <input type="number" name="timestamp" id="input-timestamp" placeholder="1619339207" />
                        </Col>
                    </Row>
                </Container>
            </div>
            <footer id="footer">
                <div id="footer-id">
                    <br />powered by <a href="#" id="footer-id-a">todolib.in</a><br /><br />
                    <p>Â© 2021 Todo Lib's & Gti<br /><br />
                    Contact Us Privacy Policy</p>
                </div>
            </footer>
        </div>
    );
}

function tick(set_tick) {
    setTimeout(() => {
        set_tick(Math.round(new Date().getTime() / 1000));
    }, 1000)
}

export default App;
